// 数组转树
let arrayToTree = (data,parentId,id)=>{
    data.sort((a,b)=>{
        return a[parentId]-b[id]
    })
    // 存储对象
    let midObj = {}
    // 从后向前遍历
    for(let i = data.length-1; i>=0; i--){
        let item = data[i]
        // 当前的父id
        let nowPid = item[parentId]
        // 当前id
        let nowId = item[id]

        // 建立当前节点的父节点的children数组
        // 如果父节点已经存在就往原数组中添加
        if(midObj[nowPid]){
            midObj[nowPid].push(item)
        }else {
            midObj[nowPid] = []
            midObj[nowPid].push(item)
        }

        // 将children放入合适的位置
        if(midObj[nowId]){
            item.children = midObj[nowId]
            delete midObj[nowId]
        }
    }
    data.filter((list)=>{
        return list[parentId] == 0
    })
    return data
}

export default {
    arrayToTree,
}
